select core_song_artist.song_id,core_song_artist.artist_id,core_playlist_song.playlist_id,core_playlist.name as playlist_name,core_song.name from core_song_artist,core_playlist_song,core_playlist,core_song where core_playlist_song.song_id = core_song_artist.song_id and core_playlist_song.playlist_id = core_playlist.id and core_playlist_song.song_id = core_song.id and artist_id = 71
