select core_playlistsongorder.playlist_id, core_playlist.name,core_song_artist.artist_id,core_artist.name as artist_name,core_artist.image as artist_image from core_playlistsongorder,core_playlist,core_song_artist,core_artist,core_playlist_song where core_playlistsongorder.playlist_id = core_playlist.id and core_playlistsongorder.song_id = core_playlist_song.song_id and core_playlistsongorder.song_id=core_song_artist.song_id and core_song_artist.artist_id = core_artist.id